void update_frame(uint8_t frame[N_ROW/2][N_COL]){

	uint8_t i, j;

	for(i = 0; i < N_ROW/2; i++){
		for(j = 0; j < N_COL; j++){
			Xil_Out32(RGB_ADDR + (i * N_COL + j) * 4, frame[i][j]);
		}
	}
}

void clean_frame(){
	uint8_t i, j;

	for(i = 0; i < N_ROW/2; i++){
		for(j = 0; j < N_COL; j++){
			new_frame[i][j] = actual_frame[i][j];
		}
	}
}

void draw_point_in_frame(uint8_t row, uint8_t col, uint8_t rgb){

	uint8_t actual_data;

	if (row < N_ROW/2) {
		new_frame[row][col] = (new_frame[row][col] & ~0x07) | (rgb & 0x07);
	}else{
		new_frame[(row - N_ROW/2)][col] = (new_frame[(row - N_ROW/2)][col] & ~0x38) | ((rgb & 0x07) << 3);
	}

}

void draw_circle_in_frame(uint8_t center_row, uint8_t center_col, uint8_t radius, uint8_t rgb, uint8_t fill) {
    int x, y;
    for (y = -radius; y <= radius; y++) {
        for (x = -radius; x <= radius; x++) {
            int dist = x * x + y * y;
            if (dist <= radius * radius) {
                if (fill || dist >= (radius - 1) * (radius - 1)) {
                    draw_point_in_frame(center_row + y, center_col + x, rgb);
                }
            }
        }
    }
}

void draw_rectangle_in_frame(uint8_t top_row, uint8_t left_col, uint8_t width, uint8_t height, uint8_t rgb, uint8_t fill) {
    uint8_t row, col;

    if (fill) {
        // Rectángulo relleno
        for (row = top_row; row < top_row + height; row++) {
            for (col = left_col; col < left_col + width; col++) {
                draw_point_in_frame(row, col, rgb);
            }
        }
    } else {
        // Solo el borde del rectángulo
        for (col = left_col; col < left_col + width; col++) {
            draw_point_in_frame(top_row, col, rgb);
            draw_point_in_frame(top_row + height - 1, col, rgb);
        }

        for (row = top_row; row < top_row + height; row++) {
            draw_point_in_frame(row, left_col, rgb);
            draw_point_in_frame(row, left_col + width - 1, rgb);
        }
    }
}

void draw_moving_circle_in_frame(uint8_t start_row, uint8_t start_col, uint8_t radius, uint8_t rgb,
									uint8_t fill, uint8_t frame_width, uint8_t frame_height, uint64_t sleep_time) {
    int8_t dx = 1;  // Dirección en X
    int8_t dy = 2;  // Dirección en Y
    uint8_t current_row = start_row;
    uint8_t current_col = start_col;
    uint8_t iterations = 0;
    while (1) {
    	clean_frame();
    	update_frame(new_frame);

        // Dibujar el círculo en su nueva posición
        draw_circle_in_frame(current_row, current_col, radius, rgb, fill);

        update_frame(new_frame);

        // Actualizar la posición del círculo
        current_row += dy;
        current_col += dx;

        // Revisar colisiones con los bordes del frame
        if (current_row - radius <= 0 || current_row + radius >= frame_height) {
            dy = -dy;  // Rebotar en el eje Y
        }
        if (current_col - radius <= 0 || current_col + radius >= frame_width) {
            dx = -dx;  // Rebotar en el eje X
        }

        usleep(sleep_time);
        iterations ++;
        if(iterations == 16){
        	rgb = ((rgb + 1) & 0x7);
        	if(rgb == 0x00)
        		rgb = 0x01;
        	iterations = 0;
        }
    }
}


